import{_ as n,V as o,n as c,p as d,q as t,J as e,C as s,A as i,a7 as r}from"./framework-a6334977.js";const h="/assets/CMInstallationProd-719e383b.png",u="/assets/CMInstallationPremium-d9f2c6bc.png",p="/assets/AutoLogout-de5c7a22.png",m="/assets/CMCLNAccount-ee9ac1a0.png",g="/assets/CMAllServers-86e209a0.png",f="data:image/png;base64,",v="/assets/CMMostPopularStates-33df2ab1.png",A="/assets/CMDiskSpaceUsage-891339b5.png",b="/assets/CMOpenFileDescriptorContextSwitches-9c921edd.png",w="/assets/CMSystemLoad-4ca4b193.png",x="/assets/CMSCPUUsag-51ecb646.png",y="/assets/CMNetworkTrafficUsage-5112d595.png",C="/assets/CMDiskSpaceUsageBasics-671a792e.png",k="/assets/CMMemoryUsage-65d01608.png",U="/assets/CMTimeSpentDoingIO-a1cc5016.png",N="/assets/CMDiskIOpsCompleted-6d0006e2.png",S="/assets/CMDiskReadWriteData-dc9348c3.png",O="/assets/CMDiskReadWriteTime-078b8c26.png",L="/assets/CMMySQLQueries-ee468a63.png",P="/assets/CMMostLoadedUsers-30b72c3c.png",M="/assets/CMUsers-6e8bc620.png",I="data:image/png;base64,",T="/assets/CMUsersCharts-6ce481bd.png",Q="/assets/CMAlertManager1-e75a5252.png",l="data:image/png;base64,",R="data:image/png;base64,",V="data:image/png;base64,",B="data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAACcAAAAdCAIAAACmIOKpAAAAA3NCSVQICAjb4U/gAAAAgklEQVRIie3VPRIAEQwGUHai0TiMSuMQDuoQuZFaYbstif0RM5uUxnjjywTdWlPL61hPsqlA3FdrRcRSyrVijIkxOuduqLrf15wz8aCUEl3lSXhw149q3FdKyFPxqn9NjqiiiirqUxVg8FZba99XQwgdGAC897Mqz0+3a19FFXVv9QTwxxy19GZ/jAAAAABJRU5ErkJggg==",E="data:image/png;base64,",q="/assets/CMAlertManager6-f9a2fecc.png",z="/assets/CMAlertManager8-9a7d9f9a.png",D={},F=r('<h1 id="centralized-monitoring" tabindex="-1"><a class="header-anchor" href="#centralized-monitoring" aria-hidden="true">#</a> Centralized Monitoring</h1><h2 id="description" tabindex="-1"><a class="header-anchor" href="#description" aria-hidden="true">#</a> Description</h2><p><span class="notranslate">Centralized Monitoring</span> is a tool that allows hosting administrators to monitor load for all their servers and users.</p><p><span class="notranslate">Centralized Monitoring</span> allows you to:</p><ul><li>View system metrics for all clients’ end servers</li><li>View the LVE statistics per user for all clients’ end servers</li><li>Enable AccelerateWP Free on all compatible servers</li></ul><h2 id="installation" tabindex="-1"><a class="header-anchor" href="#installation" aria-hidden="true">#</a> Installation</h2><div class="tip custom-block"><p class="custom-block-title">Note</p><p>Make sure that <code>cm.cloudlinux.com</code> is available on your end server.</p></div>',7),H=r(`<li>Make sure you have a CloudLinux OS Shared Pro subscription.</li><li>Make sure you have installed the latest <strong>lve-utils</strong> package. You can install or update it with the following commands: <ul><li>installation</li></ul><div class="language-text line-numbers-mode" data-ext="text"><pre class="language-text"><code>yum install lve-utils
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><ul><li>update</li></ul><div class="language-text line-numbers-mode" data-ext="text"><pre class="language-text"><code>yum update lve-utils
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li><li>Log in to the <a href="https://cm.cloudlinux.com/" target="_blank" rel="noopener noreferrer">https://cm.cloudlinux.com/</a> using CLN credentials (if you are already logged in via CLN, authorization via CM is not necessary, it uses SSO).</li>`,3),W=t("a",{href:"https://cm.cloudlinux.com",target:"_blank",rel:"noopener noreferrer"},"Centralized Monitoring UI",-1),K=t("a",{href:"https://cln.cloudlinux.com/console/cloudlinux/centralized-monitoring",target:"_blank",rel:"noopener noreferrer"},"CLN UI",-1),j=t("img",{src:h,alt:""},null,-1),Y=t("img",{src:u,alt:""},null,-1),J=r(`<li>Make sure you have activated statistics collection (see paragraph 4) otherwise you will not be able to set up your servers. For instant set up of a registered server after statistics collection was enabled, run the following commands for all servers:<div class="language-text line-numbers-mode" data-ext="text"><pre class="language-text"><code>rhn_check	
/usr/share/cloudlinux/cl_plus/manage_clplus enable
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div></div></div><strong>Note</strong>: If the <code>rhn_check</code> command is not found, run the following command:<div class="language-text line-numbers-mode" data-ext="text"><pre class="language-text"><code>yum install/update rhn-check rhn-setup
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div>AccelerateWP Premium activation support was added in <code>lve-utils-6.5.11-1</code>.</li><li>After 5 hours (or after the manual setup), check that statistics for all registered servers is collected via <a href="https://cm.cloudlinux.com/#/servers" target="_blank" rel="noopener noreferrer">https://cm.cloudlinux.com/#/servers</a>. And check that user statistics on the servers is collected via <a href="https://cm.cloudlinux.com/#/users" target="_blank" rel="noopener noreferrer">https://cm.cloudlinux.com/#/users</a>.<div class="tip custom-block"><p class="custom-block-title">Note</p><p>User statistics will be available only for users that were loaded starting from connecting the server to the Centralized Monitoring.</p></div></li>`,2),X=r('<h2 id="centralized-monitoring-mode-without-session-expired" tabindex="-1"><a class="header-anchor" href="#centralized-monitoring-mode-without-session-expired" aria-hidden="true">#</a> Centralized Monitoring: mode without session expired</h2><p>Users can monitor server’s or user’s load for a long time using the mode without session expired.</p><p>To turn on the mode without session expired, follow the next steps:</p><ol><li><p>Log in to the <a href="https://cln.cloudlinux.com/console/" target="_blank" rel="noopener noreferrer">cln.cloudlinux.com</a> via your account</p></li><li><p>Open the <a href="https://cm.cloudlinux.com/#/servers" target="_blank" rel="noopener noreferrer">cm.cloudlinux.com</a> in a new browser tab/window (please, use the same browser as in step 1)</p></li><li><p>Use the toggle to turn on/off 10 min auto logout</p><p><img src="'+p+'" alt=""></p></li></ol><p>Your session in the <a href="https://cln.cloudlinux.com/console/" target="_blank" rel="noopener noreferrer">cln.cloudlinux.com</a> will expire in 10 min. But your session in the <a href="https://cm.cloudlinux.com/#/servers" target="_blank" rel="noopener noreferrer">cm.cloudlinux.com</a> will not expire while your browser tab remains open.</p><h2 id="centralized-monitoring-user-interface" tabindex="-1"><a class="header-anchor" href="#centralized-monitoring-user-interface" aria-hidden="true">#</a> Centralized Monitoring user interface</h2><p>You can access <span class="notranslate">Centralized Monitoring</span> in your <a href="https://cln.cloudlinux.com/" target="_blank" rel="noopener noreferrer">CLN account</a>. Click <span class="notranslate">C-Monitoring</span> in the left menu.</p><p><img src="'+m+'" alt=""></p><h3 id="servers" tabindex="-1"><a class="header-anchor" href="#servers" aria-hidden="true">#</a> Servers</h3>',9),_=r('<p><img src="'+g+'" alt=""></p><p>The following values are available for each server:</p><ul><li><span class="notranslate"><strong>Load Avg 15m</strong></span> – average system load for the last 15 min</li><li><span class="notranslate"><strong>CPU Usage</strong></span> – CPU usage for the last 15 min (the number of cores can be found in the hint)</li><li><span class="notranslate"><strong>Memory Usage</strong></span> – free available memory, the second value is the total memory for the last 15 min</li><li><span class="notranslate"><strong>IO read/write</strong></span> – disk read bytes/disk written bytes for the last 15 min</li></ul><div class="tip custom-block"><p class="custom-block-title">Note</p><p>The values are calculated using a 15 min time period but the metric state is updated automatically every minute by default or you can choose from one of the predefined periods.</p></div><ul><li><span class="notranslate"><strong>Idle state</strong></span> – there were no statistics for the server for the last minute.</li><li><span class="notranslate"><strong>N/A state</strong></span> – there were no statistics for the server for the last 30 days. This can happen if a new server was added but statistics sending was not configured.</li></ul><p>There is no pagination on the <span class="notranslate"><em>All servers</em></span> page and all columns can be sorted by absolute value. Use the search tool to operate with the data.</p><h3 id="servers-details" tabindex="-1"><a class="header-anchor" href="#servers-details" aria-hidden="true">#</a> Servers details</h3><p>To get the detailed statistics for the server via charts, click a desired server line in the table. All charts are auto-refreshed and there is an ability to select the period for metrics data to be updated for the chart.</p><p><img src="'+f+'" alt=""></p><div class="warning custom-block"><p class="custom-block-title">Note</p><p>We store the metrics data for one month only.</p></div><h3 id="charts-for-server-metrics" tabindex="-1"><a class="header-anchor" href="#charts-for-server-metrics" aria-hidden="true">#</a> Charts for server metrics</h3><h3 id="visualization-of-the-most-popular-server-states" tabindex="-1"><a class="header-anchor" href="#visualization-of-the-most-popular-server-states" aria-hidden="true">#</a> Visualization of the most popular server states</h3><p><img src="'+v+'" alt=""></p><h3 id="disk-space-usage" tabindex="-1"><a class="header-anchor" href="#disk-space-usage" aria-hidden="true">#</a> Disk space usage</h3><p><img src="'+A+'" alt=""></p><h3 id="open-file-descriptor-context-switches" tabindex="-1"><a class="header-anchor" href="#open-file-descriptor-context-switches" aria-hidden="true">#</a> Open file descriptor/Context switches</h3><p><img src="'+b+'" alt=""></p><h3 id="system-load-1m-5m-15m" tabindex="-1"><a class="header-anchor" href="#system-load-1m-5m-15m" aria-hidden="true">#</a> System load 1m , 5m, 15m</h3><p><img src="'+w+'" alt=""></p><h3 id="cpu-usage-total-system-user-iowait-steal" tabindex="-1"><a class="header-anchor" href="#cpu-usage-total-system-user-iowait-steal" aria-hidden="true">#</a> CPU usage (total, system, user, iowait, steal)</h3><p><img src="'+x+'" alt=""></p><h3 id="network-traffic-usage" tabindex="-1"><a class="header-anchor" href="#network-traffic-usage" aria-hidden="true">#</a> Network traffic usage</h3><p><img src="'+y+'" alt=""></p><h3 id="disk-space-usage-1" tabindex="-1"><a class="header-anchor" href="#disk-space-usage-1" aria-hidden="true">#</a> Disk space usage</h3><p><img src="'+C+'" alt=""></p><h3 id="memory-usage-total-used-available" tabindex="-1"><a class="header-anchor" href="#memory-usage-total-used-available" aria-hidden="true">#</a> Memory usage (total, used, available)</h3><p><img src="'+k+'" alt=""></p><h3 id="time-spent-doing-i-os" tabindex="-1"><a class="header-anchor" href="#time-spent-doing-i-os" aria-hidden="true">#</a> Time spent doing I/Os</h3><p><img src="'+U+'" alt=""></p><h3 id="disk-iops-completed" tabindex="-1"><a class="header-anchor" href="#disk-iops-completed" aria-hidden="true">#</a> Disk IOps Completed</h3><p><img src="'+N+'" alt=""></p><h3 id="disk-read-write-data" tabindex="-1"><a class="header-anchor" href="#disk-read-write-data" aria-hidden="true">#</a> Disk read/write data</h3><p><img src="'+S+'" alt=""></p><h3 id="disk-read-write-time" tabindex="-1"><a class="header-anchor" href="#disk-read-write-time" aria-hidden="true">#</a> Disk read/write time</h3><p><img src="'+O+'" alt=""></p><h3 id="apache-connections-number-number-of-requests-per-minute-max-connections" tabindex="-1"><a class="header-anchor" href="#apache-connections-number-number-of-requests-per-minute-max-connections" aria-hidden="true">#</a> Apache connections (number)/Number of requests per minute/Max connections</h3><div class="warning custom-block"><p class="custom-block-title">Note</p><p>In the current version, we collect these metrics for the cPanel end servers only. We are planning to add other panels support soon.</p></div><div class="warning custom-block"><p class="custom-block-title">Note</p><p>In the current version, we collect these metrics only for Apache (NOT for LiteSpeed, Nginx, etc.). The charts will be empty for LiteSpeed, Nginx, etc..</p></div><h3 id="mysql-queries" tabindex="-1"><a class="header-anchor" href="#mysql-queries" aria-hidden="true">#</a> MySQL queries</h3><p><img src="'+L+'" alt=""></p><p>MySQL queries collector gets number of queries executed on the server per minute. It takes data from the MySQL server variable &quot;Questions&quot;. You may manually check variable value by executing query <code>SHOW GLOBAL STATUS LIKE &#39;Questions&#39;;</code>. For more information about MySQL server variables - please, see MySQL documentation.</p><h3 id="the-most-loaded-server-users-for-the-last-minute" tabindex="-1"><a class="header-anchor" href="#the-most-loaded-server-users-for-the-last-minute" aria-hidden="true">#</a> The most loaded server users for the last minute</h3><p><img src="'+P+'" alt=""></p><p>We calculate the user’s load by LVE statistics that we collect on the end server. The idle state for the user means that the LVE statistics were not collected for the last minute for some reason.</p><p>In each cell there are current usage/limit values for the basic LVE limits:</p><ul><li>CPU Usage</li><li>Entry Processes</li><li>Physical Memory Usage</li><li>IOPS</li><li>IO Usage</li><li>Number of Processes</li><li>MySQL CPU</li><li>MySQL IO</li></ul><p>In the hint, there is a number of faults for each limit. The values in the columns are underlined (it is red if load-to-limit ratio &gt;=90% and it is yellow if load-to-limit ratio &gt;= 50%). For the current implementation, the only sort by the load-to-limit ratio is available. By default, there is a descending sort by the CPU usage column.</p><p>When sorting by a column, the lines with the load-to-limit ratio &gt;=90% for this column will have the red background color, and lines with the load-to-limit ratio &gt;=50% for this column will have the yellow background color.</p><div class="tip custom-block"><p class="custom-block-title">Note</p><p>The users with unlimited resources (∞) will be at the bottom of the table.</p></div><h3 id="users" tabindex="-1"><a class="header-anchor" href="#users" aria-hidden="true">#</a> Users</h3><p>This page contains all users for the all server of the client and their LVE statistics for the last minute. You can select the number of users on this page and search by user’s data.</p>',51),G=t("em",null,"The most loaded server users for the last minute",-1),Z=r('<p><img src="'+M+'" alt=""></p><p>User’s metrics data can be sorted by the load-to-limit ratio and by the absolute value.</p><p>The absolute value is used to analyse the load produced by unlimited users.</p><p>The value of the load-to-limit ratio is convenient to use in the analysis of how many resources the users consume and whether they need to change the limits.</p><p>The values like this <img src="'+I+'" alt=""> means that the resource is unlimited and 500.2 MB is the current usage of it.</p><p>Metrics data of <em>Idle users</em> is not used in the sorting, so such users always will be at the end of the list. The sorting can be done for only one metric.</p><h3 id="charts-for-users-metrics" tabindex="-1"><a class="header-anchor" href="#charts-for-users-metrics" aria-hidden="true">#</a> Charts for Users metrics</h3><div class="warning custom-block"><p class="custom-block-title">Note</p><p>We store the metrics data for one month only.</p></div><p>On the user details page, the admin can find the charts for all LVE limits.</p><p><img src="'+T+'" alt=""></p><h2 id="alert-manager" tabindex="-1"><a class="header-anchor" href="#alert-manager" aria-hidden="true">#</a> Alert Manager</h2><p>Alert Manager allows you to create a server or user alert for selected metrics and email the triggered events.</p><h3 id="alert-manager-page" tabindex="-1"><a class="header-anchor" href="#alert-manager-page" aria-hidden="true">#</a> Alert Manager page</h3><p><img src="'+Q+'" alt=""></p><p>The Alert Manager page contains a table with the following:</p><ul><li><strong>Alert name</strong> - a unique alert name</li><li><strong>Tracking metric</strong> - a name of a server/user metric which will trigger the alert notification</li><li><strong># of servers</strong> - number of servers on which the metric will be tracked <ul><li>click <img src="'+l+'" alt=""> to view a list of servers host names</li></ul></li><li><strong># of users</strong> - number of users for which the metric will be tracked <ul><li>click <img src="'+l+'" alt=""> to view a list of users names</li></ul></li><li><strong>Value</strong> - a condition for the alert rule which will be applied to the tracking metrics</li><li><strong>Email</strong> - email to send the triggered events notifications</li><li><strong>Type</strong> - a type of the alert rule</li><li><strong># of triggered events</strong> - the number of events from the time, when alert rule was created <ul><li><img src="'+R+'" alt=""> the event is still firing</li></ul></li><li><strong>Time of the last trigger</strong> - the time of last triggered event, it is the time in your browser time zone</li><li><strong>Actions</strong> - click <img src="'+V+'" alt=""> to edit and <img src="'+B+'" alt=""> to delete the alert rule</li></ul><p><strong>Color Codes</strong></p><ul><li><strong>Red</strong> color means that the event with the condition &quot;more than&quot; is still firing.</li><li><strong>Green</strong> color means that the event with the condition &quot;less than&quot; is still firing.</li></ul><h3 id="creating-an-alert" tabindex="-1"><a class="header-anchor" href="#creating-an-alert" aria-hidden="true">#</a> Creating an alert</h3><p>To create a new alert, click the <em>Create alert</em> button.</p><p><img src="'+E+'" alt=""></p><p>Next, fill out the opened popup.</p><p><img src="'+q+'" alt=""></p>',23),$=t("li",null,[t("strong",null,"Name of alert"),e(" - a unique alert name")],-1),ee=t("strong",null,"Alert type",-1),te=t("strong",null,"user",-1),ae=t("strong",null,"server",-1),se=t("strong",null,"Select user/server",-1),ie=t("li",null,[t("strong",null,"Notify me"),e(" - the condition of the alert trigger")],-1),re=t("li",null,[t("strong",null,"Duration"),e(" - how much time the condition should be actual to trigger the notification")],-1),le=t("li",null,[t("strong",null,"Notify me on email"),e(" - the email to send notifications")],-1),ne=r('<h3 id="editing-an-alert" tabindex="-1"><a class="header-anchor" href="#editing-an-alert" aria-hidden="true">#</a> Editing an alert</h3><p>An admin can edit any field in the Alert except the Alert type.</p><h3 id="difference-between-the-server-alert-and-the-user-alert" tabindex="-1"><a class="header-anchor" href="#difference-between-the-server-alert-and-the-user-alert" aria-hidden="true">#</a> Difference between the server alert and the user alert</h3><p>The <strong>server alert</strong> is used to track the state of the whole server, it does not track user state on the server. The <strong>server alert</strong> tracks the next list of metrics:</p><ol><li>Context switches</li><li>System load (1m)</li><li>System load (5m)</li><li>System load (15m)</li><li>CPU Basic (total)</li><li>CPU Basic (system)</li><li>CPU Basic (user)</li><li>CPU Basic (iowait)</li><li>CPU Basic (steal)</li><li>Network Traffic Basic (<code>eht0_receive</code>)</li><li>Network Traffic Basic (<code>eht0_transmit</code>)</li><li>Network Traffic Basic (<code>ehtN_receive</code>)</li><li>Network Traffic Basic (<code>ehtN_transmit</code>)</li><li>Disk Space Used Basic (<code>mountpoint: &lt;0&gt;</code>)</li><li>Disk Space Used Basic (<code>mountpoint: &lt;1&gt;</code>)</li><li>Disk Space Used Basic (<code>mountpoint: &lt;N&gt;</code>)</li><li>Memory Basic (available)</li><li>Memory Basic (used)</li><li>Time spent Doing I/Os</li><li>Disk IOps Writes Completed</li><li>Disk IOps Reads Completed</li><li>Disk Read Data</li><li>Disk Write Data</li><li>Disk Read Time</li><li>Disk Write Time</li><li>Apache connections</li><li>Number of requests per minute</li><li>MySQL queries</li><li>Hardware Temperature (<code>chip&lt;0&gt;</code>)</li><li>Hardware Temperature (<code>sensor&lt;0&gt;</code>)</li><li>Hardware Temperature (<code>chip&lt;N&gt;</code>)</li><li>Hardware Temperature (<code>sensor&lt;N&gt;</code>)</li><li>Open File Description</li></ol><p>During creating a server alert an admin should select the type of metrics as the first step. The list of servers will be collected according to the availability of these metrics on the server.</p><p>For example, for now, we do not collect Apache metrics for non-cPanel servers, so you will get only cPanel servers as a list of servers for these metrics.</p><p>We&#39;re planning to implement support for other panels/web servers in the next releases.</p><div class="tip custom-block"><p class="custom-block-title">Small limitation</p><p>We collect the server list according to having their statistics in our database (this behavior will be changed in the next releases).</p></div><p>For example, if server state is N/A or idle more than 24 hours, it will not be visible in the list for the alert.</p><p>The <strong>user alert</strong> tracks the next list of LVE metrics:</p><ol><li>CPU Usage (current usage)</li><li>CPU Usage (faults)</li><li>Entry Processes (current usage)</li><li>Entry Processes (faults)</li><li>Physical Memory Usage (current usage)</li><li>Physical Memory Usage (faults)</li><li>IOPS (current usage)</li><li>IOPS (faults)</li><li>IO Usage (current usage)</li><li>IO Usage (faults)</li><li>Number of Processes (current usage)</li><li>Number of Processes (faults)</li><li>MySQL CPU (current usage)</li><li>MySQL CPU (faults)</li><li>MySQL IO (current usage)</li></ol><div class="tip custom-block"><p class="custom-block-title">Small limitation</p><p>We collect the server list according to having their statistics in our database (this behavior will be changed in the next releases).</p></div><p>For example, if the user state is N/A or idle more than 24 hours, it will not be visible in the list for the alert.</p><h3 id="cases-of-alert-creating" tabindex="-1"><a class="header-anchor" href="#cases-of-alert-creating" aria-hidden="true">#</a> Cases of alert creating</h3><ul><li>Creating a server alert for the selected metrics for one server</li><li>Creating a server alert for the selected metrics for all servers (the default value)</li></ul><p>In this two cases, you will not see the dropdown for selecting users because the metrics will track the server state.</p><ul><li>Creating a user alert for one user, so admin can select a server and a user.</li><li>Creating a user alert for all users on several servers/all servers (in this case admin can&#39;t select users - all users will be selected automatically)</li></ul><h3 id="what-is-the-firing-state-of-the-alert" tabindex="-1"><a class="header-anchor" href="#what-is-the-firing-state-of-the-alert" aria-hidden="true">#</a> What is the Firing state of the alert?</h3><p>This is the state of an alert that has been active for longer than the configured threshold duration.</p><h3 id="alert-notifications" tabindex="-1"><a class="header-anchor" href="#alert-notifications" aria-hidden="true">#</a> Alert notifications</h3><p><img src="'+z+`" alt=""></p><ul><li><strong>Alert name</strong> - the link to the alert page</li><li><strong>Firing target</strong> - the link to the server details page</li></ul><h2 id="faq" tabindex="-1"><a class="header-anchor" href="#faq" aria-hidden="true">#</a> FAQ</h2><h3 id="how-can-i-disable-collecting-and-sending-statistics-on-a-client-s-server" tabindex="-1"><a class="header-anchor" href="#how-can-i-disable-collecting-and-sending-statistics-on-a-client-s-server" aria-hidden="true">#</a> How can I disable collecting and sending statistics on a client’s server?</h3><p>Run this command:</p><div class="notranslate"><div class="language-text line-numbers-mode" data-ext="text"><pre class="language-text"><code>/usr/share/cloudlinux/cl_plus/manage_clplus disable
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></div><h3 id="where-can-i-view-all-my-servers-load" tabindex="-1"><a class="header-anchor" href="#where-can-i-view-all-my-servers-load" aria-hidden="true">#</a> Where can I view all my servers load?</h3><p>You can find all your servers load in your CM personal account here: <a href="https://cm.cloudlinux.com/#/servers" target="_blank" rel="noopener noreferrer">https://cm.cloudlinux.com/#/servers</a> or in your CLN personal account here: <a href="https://cln.cloudlinux.com/console/cloudlinux/centralized-monitoring" target="_blank" rel="noopener noreferrer">https://cln.cloudlinux.com/console/cloudlinux/centralized-monitoring</a>.</p><h3 id="where-can-i-view-all-my-users-load" tabindex="-1"><a class="header-anchor" href="#where-can-i-view-all-my-users-load" aria-hidden="true">#</a> Where can I view all my users load?</h3><p>You can find all your users load in your CM personal account here: <a href="https://cm.cloudlinux.com/#/users" target="_blank" rel="noopener noreferrer">https://cm.cloudlinux.com/#/users</a> or in your CLN personal account here: <a href="https://cln.cloudlinux.com/console/cloudlinux/centralized-monitoring" target="_blank" rel="noopener noreferrer">https://cln.cloudlinux.com/console/cloudlinux/centralized-monitoring</a></p><h3 id="where-can-i-view-a-server-load-details-for-a-period" tabindex="-1"><a class="header-anchor" href="#where-can-i-view-a-server-load-details-for-a-period" aria-hidden="true">#</a> Where can I view a server load details for a period?</h3><p>Click the desired server in the server list in the UI.</p><h3 id="where-can-i-view-a-user-load-details-for-the-period" tabindex="-1"><a class="header-anchor" href="#where-can-i-view-a-user-load-details-for-the-period" aria-hidden="true">#</a> Where can I view a user load details for the period?</h3><p>Click the desired user in the user list in the UI.</p><h3 id="how-long-are-statistics-stored-in-the-central-database" tabindex="-1"><a class="header-anchor" href="#how-long-are-statistics-stored-in-the-central-database" aria-hidden="true">#</a> How long are statistics stored in the central database?</h3><p>30 days.</p><h3 id="how-can-i-change-the-charts-period-on-the-details-page" tabindex="-1"><a class="header-anchor" href="#how-can-i-change-the-charts-period-on-the-details-page" aria-hidden="true">#</a> How can I change the charts period on the details page?</h3><p>Choose the desired period in the upper right corner or select it directly on the chart.</p><h3 id="i-don-t-understand-how-to-read-the-user-load-chart" tabindex="-1"><a class="header-anchor" href="#i-don-t-understand-how-to-read-the-user-load-chart" aria-hidden="true">#</a> I don’t understand how to read the user load chart.</h3><p>The user load chart contains three lines:</p><ul><li>limit</li><li>current load</li><li>count of faults</li></ul><p>Limit and current load are drawing regarding the left vertical axis, the count of faults is drawing regarding the right vertical axis. You can focus on a particular line by clicking a required legend.</p><h2 id="troubleshooting" tabindex="-1"><a class="header-anchor" href="#troubleshooting" aria-hidden="true">#</a> Troubleshooting</h2><h3 id="i-can-t-see-a-server-statistics" tabindex="-1"><a class="header-anchor" href="#i-can-t-see-a-server-statistics" aria-hidden="true">#</a> I can&#39;t see a server statistics</h3><ol><li>Check that your server is registered by key or by IP license of the CloudLinux+ account, i.e., it should be seen in the list of servers in your CLN account here: <a href="https://cln.cloudlinux.com/console/auth/login" target="_blank" rel="noopener noreferrer">https://cln.cloudlinux.com/console/auth/login</a></li><li>Check that the following required packages are installed on the end server:</li></ol><ul><li><span class="notranslate"><code>cl-end-server-tools</code></span> &gt;= 1.0.7-1</li><li><span class="notranslate"><code>cl-node-exporter</code></span> &gt;= 1.1.0-2</li><li><span class="notranslate"><code>rhn-client-tools</code></span><ul><li>CloudLinux OS 6 &gt;= 1.1.15-3.el6.cloudlinux.26</li><li>CloudLinux OS 7 &gt;= 2.0.2-31.el7.clouldinux</li><li>CloudLinux OS 8 &gt;= 2.8.16-14.module_el8.1.0+6074+9dc6073e.cloudlinux.2</li></ul></li><li><span class="notranslate"><code>lve-stats</code></span> &gt;= 3.0.7-2</li><li><span class="notranslate"><code>lve-utils</code></span> &gt;= 4.2.21-2</li><li><span class="notranslate"><code>alt-python27-cllib</code></span> &gt;= 2.1.13-1</li><li><code>lvemanager</code> &gt;= 6.2.10-1</li></ul><ol start="3"><li>Check that service collecting and sending statistics is running:</li></ol><div class="notranslate"><div class="language-text line-numbers-mode" data-ext="text"><pre class="language-text"><code>service cl_plus_sender status
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></div><ol start="4"><li>Check that log of the <span class="notranslate"><em>cl_plus_sender</em></span> service doesn&#39;t contain errors:</li></ol><div class="notranslate"><div class="language-text line-numbers-mode" data-ext="text"><pre class="language-text"><code>/var/log/clplus_sender.log
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></div><h3 id="where-can-i-view-the-events-log-on-the-client-s-server" tabindex="-1"><a class="header-anchor" href="#where-can-i-view-the-events-log-on-the-client-s-server" aria-hidden="true">#</a> Where can I view the events log on the client&#39;s server?</h3><p>You can view the events log on the client&#39;s server here:</p><div class="notranslate"><div class="language-text line-numbers-mode" data-ext="text"><pre class="language-text"><code>/var/log/clplus_sender.log
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></div><h3 id="can-i-get-monitoring-metrics-from-litespeed-nginx-or-other-not-apache-web-server" tabindex="-1"><a class="header-anchor" href="#can-i-get-monitoring-metrics-from-litespeed-nginx-or-other-not-apache-web-server" aria-hidden="true">#</a> Can I get monitoring metrics from LiteSpeed, Nginx or other (Not Apache) web server?</h3><p>Starting from end-server-tools-1.0.7, it supports collecting and sending statistics from the Apache and LiteSpeed web servers.</p><p>LiteSpeed is supported on cPanel and DirectAdmin control panels.</p><p>Each minute the statistics collection daemon checks which web server is started. If LiteSpeed is started, the daemon will collect data from it, otherwise, it checks if Apache is started.</p><p>When the daemon detects that the server is changed, it writes the following line into the statistics collection daemon log <code>/var/log/clplus_sender.log</code>:</p><div class="language-text line-numbers-mode" data-ext="text"><pre class="language-text"><code>2020-10-09 17:25:31,462: (CL+_sender_daemon) [INFO] Apache/Litespeed collector: Using Apache
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>or</p><div class="language-text line-numbers-mode" data-ext="text"><pre class="language-text"><code>2020-10-09 18:13:03,897: (CL+_sender_daemon) [INFO] Apache/Litespeed collector: Using Litespeed
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>If the daemon can&#39;t detect either Apache or LiteSpeed, it writes to the log the following:</p><div class="language-text line-numbers-mode" data-ext="text"><pre class="language-text"><code>2020-10-09 17:33:38,399: (CL+_sender_daemon) [INFO] Apache/Litespeed collector: Apache or Litespeed stopped or absent, collector will not work
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>The statistics collection daemon reacts to the server changing automatically, no need to restart it.</p><div class="warning custom-block"><p class="custom-block-title">Warning</p><p>Please note that the daemon checks the server type once in a minute, so the data sent on a minute of switching can be unreliable.</p></div><h3 id="logging-data-sent-to-pushgateway-to-the-statistics-collection-daemon-log" tabindex="-1"><a class="header-anchor" href="#logging-data-sent-to-pushgateway-to-the-statistics-collection-daemon-log" aria-hidden="true">#</a> Logging data sent to pushgateway to the statistics collection daemon log</h3><p>Starting from <code>cl-end-server-tools</code> v.1.0.6-1, the statistics collection daemon allows to log data sent to pushgateway to its log <code>/var/log/clplus_sender.log</code>.</p><p>To start logging, run the following command:</p><div class="language-text line-numbers-mode" data-ext="text"><pre class="language-text"><code>touch /var/lve/cmt_debug_logging
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>To stop logging, run the following command:</p><div class="language-text line-numbers-mode" data-ext="text"><pre class="language-text"><code>rm -f /var/lve/cmt_debug_logging
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>You don&#39;t need to restart the daemon after starting/stopping logging. The presence of a control file is evaluated &quot;on the fly&quot;.</p><div class="warning custom-block"><p class="custom-block-title">Warning</p><p>Use this logging with caution because when it is enabled, the size of the daemon log <code>/var/log/clplus_sender.log</code> will increase each minute minimum on 3-4 KB. The actual increase size depends on the number of active users&#39; processes on a server.</p></div><h2 id="known-issues" tabindex="-1"><a class="header-anchor" href="#known-issues" aria-hidden="true">#</a> Known issues</h2><ul><li>MySQL Governor statistics in some cases is collected incorrectly</li><li>Sorting by MySQL Governor statistics ignores idle users</li><li>Sorting from the search result set does not work</li><li>Sorting by ratio for unlimited users works incorrectly</li></ul>`,76);function oe(ce,de){const a=o("RouterLink");return c(),d("div",null,[F,t("ol",null,[H,t("li",null,[e("Activate statistics collection on all your servers via the "),W,e(" or via the "),K,e(". Optionally, activate the "),s(a,{to:"/shared-pro/accelerate-wp/#acceleratewp-suite"},{default:i(()=>[e("AccelerateWP Free")]),_:1}),e("* for all of your compatible servers. "),j,e(" Additionally, it is possible to activate the "),s(a,{to:"/shared-pro/accelerate-wp/#acceleratewp-premium-suite"},{default:i(()=>[e("AccelerateWP Premium")]),_:1}),e("* for all compatible servers. "),Y]),t("li",null,[e("Within couple minutes after the activation, statistics collection and sending to the central server, "),s(a,{to:"/shared-pro/accelerate-wp/#acceleratewp-suite"},{default:i(()=>[e("AccelerateWP Free")]),_:1}),e("* and "),s(a,{to:"/shared-pro/accelerate-wp/#acceleratewp-premium-suite"},{default:i(()=>[e("AccelerateWP Premium")]),_:1}),e(" * will be set up automatically: all required packages and components will be installed. For new, just registered servers, actions can take up to 5 hours.")]),J]),t("p",null,[e("* AccelerateWP Free activation also automatically installs and configures "),s(a,{to:"/legacy/command-line_tools/#x-ray-autotracing"},{default:i(()=>[e("Autotracing")]),_:1}),e(" on server.")]),X,t("p",null,[e("This page contains the list of all clients’ end servers. The server appears in the list after finishing "),s(a,{to:"/shared-pro/centralized-monitoring/#installation-2"},{default:i(()=>[e("Installation")]),_:1}),e(". By default, there is a descending sort by CPU usage.")]),_,t("p",null,[e("The description of this page is the same as "),s(a,{to:"/shared-pro/centralized-monitoring/#the-most-loaded-server-users-for-the-last-minute"},{default:i(()=>[G]),_:1}),e(" of the top 5 loaded users.")]),Z,t("ul",null,[$,t("li",null,[ee,e(" - an admin can create a "),te,e(" or a "),ae,e(" alert. "),s(a,{to:"/shared-pro/centralized-monitoring/#difference-between-the-server-alert-and-the-user-alert"},{default:i(()=>[e("What is the difference between them?")]),_:1})]),t("li",null,[se,e(" - admin will see such dropdown depending on a "),s(a,{to:"/shared-pro/centralized-monitoring/#cases-of-alert-creating"},{default:i(()=>[e("case of alert creating")]),_:1})]),ie,re,le]),ne])}const ue=n(D,[["render",oe],["__file","index.html.vue"]]);export{ue as default};
